        if (q == "region") {
            var min = list.region[0].id;
            var max = list.region.length + min;
            i = parseInt(Math.random() * (max - min) + min);
        } else if (q == "river") {
            var min = list.river[0].id;
            var max = list.river.length + min;
            i = parseInt(Math.random() * (max - min) + min);
        } else if (q == "city") {
            var min = list.city[0].id;
            var max = list.city.length + min;
            i = parseInt(Math.random() * (max - min) + min);
        } else if (q == "place") {
            var min = list.place[0].id;
            var max = list.place.length + min;
            i = parseInt(Math.random() * (max - min) + min);
        } else i = -1;
        console.log(i);
        console.log(this.map.current);

        while (!list.river[i] && m < 100) {
            console.log(list.river[i]);
            i = Math.random();
            m = m + 1;

        }
