import React, { Component } from 'react';
import { ReactComponent as Map } from '../components/Map.svg';
import list from '../components/list.json';

export default class Learn extends Component {
	constructor(props) {
		super(props);
		this.state = {
			login: "",
			password: "",
			role: "",
			loginError: "",
			current: {},
			data: list,
			check: {
				name: '',
				data: []
			},
			results: {
				name: '',
				res: []
			}
		};
		this.map = React.createRef();
		this.handleClick = this.handleClick.bind(this);
		this.handleChoose = this.handleChoose.bind(this);
		this.handleStart = this.handleStart.bind(this);

	}

	handleStart() {
		var i;
		var min = 0;
		var max = 1;
		var q = document.getElementsByClassName("chosen")[0].getAttribute("class").split(" ")[0];
		// console.log(this.state.check,q)
		if (this.state.check.name !== q) {

			this.setState({
				results: {
					name: q,
					res: []
				}
			});
		}

		if (this.state.data[q].length <= this.state.check.length) {
			i = -1;
			this.setState({
				current: {
					name: ""
				}
			});
		} else {
			do {
				min = this.state.data[q][0].id;
				max = this.state.data[q].length + min;
				i = parseInt(Math.random() * (max - min) + min);
				i = i - min;
				this.setState({
					current: {
						id: this.state.data[q][i].id,
						name: this.state.data[q][i].name,
						try: 0
					}
				});
			// console.log(this.state.check)
			} while (this.state.check.data.includes(this.state.data[q][i].id))
			// console.log(this.state.check);
			// console.log(list[q]);
			// console.log(this.state.data[q]);
			this.setState({
				check: {
					name: q,
					data: this.state.check.data.concat([this.state.data[q][i].id])
				}
			})
			// this.setState({ results:{name: name,  res: this.state.results.res.concat([this.state.current]) } });

		// this.setState({ check: this.state.check.concat([this.state.data[q][i]]) })
		// this.state.data[q].splice(i, 1);
		}

	}


	handleClick(event) {
		console.log(this.state.check);
		var layer = event.target.parentElement.getAttribute("id");


		//console.log(event.target.getAttribute('name'))
		//console.log(event.target.getAttribute('id'))

		//console.log(event.target.parentElement.getAttribute("id"));
		//console.log(document.getElementsByClassName("chosen")[0].getAttribute("class").split(" ")[0]);
		if (!this.state.current.id) {
			return
		}
		if (layer !== document.getElementsByClassName("chosen")[0].getAttribute("class").split(" ")[0]) {
			return;
		}
		if (parseInt(event.target.getAttribute("id")) !== this.state.current.id) {
			console.log(this.state.current);
			if (!event.target.classList.contains("right")) {
				this.setState({
					current: {
						id: this.state.current.id,
						name: this.state.current.name,
						try: this.state.current.try + 1
					}
				});
			}
			event.target.classList.add('wrong');
			return;
		} else {
			event.target.classList.remove('wrong');
			event.target.classList.add('right');
			console.log(this.state.current)
			var name = document.getElementsByClassName("chosen")[0].getAttribute("class").split(" ")[0];
			this.setState({
				results: {
					name: name,
					res: this.state.results.res.concat([this.state.current])
				}
			});
			this.setState({
				results: {
					name: name,
					res: this.state.results.res.concat([this.state.current])
				}
			});
		}
		this.handleStart();
	}


	handleChoose(event) {
		// this.setState({ data: list });
		this.setState({
			check: {
				name: '',
				data: []
			}
		})
		this.setState({
			results: {
				name: '',
				res: []
			}
		})
		// console.log('resret')
		// console.log(this.state.check)
		if (document.getElementsByClassName("chosen")[0]) {
			var i = document.getElementsByClassName("chosen")[0];
			if (event.target.classList.contains(i.getAttribute("class").split(" ")[0])) {
				return;
			}
			i.classList.remove("chosen");
			i = i.getAttribute("class").split(" ")[0];
			this.reset(i);
			document.getElementById(i).style.display = "none";
		}
		document.getElementById("region").style.display = "inline";
		document.getElementById(event.target.className).style.display = "inline";
		document.getElementsByTagName("svg")[0].getElementsByClassName("active")[0].classList.remove("active");
		document.getElementsByTagName("svg")[0].getElementById(event.target.className).classList.add("active");
		event.target.classList.add("chosen");
		this.handleStart();

	}

	reset(s) {
		var a = document.getElementsByClassName("wrong");
		var b = document.getElementsByClassName("right");
		console.log(s)
		// Array.prototype.filter.call(testElements, function(testElement) {
		// return testElement.nodeName === 'DIV';
		// });????????????
		for (var i = a.length - 1; i >= 0; i--) {
			a[i].classList.remove('wrong');
		}
		for (var j = b.length - 1; j >= 0; j--) {
			b[j].classList.remove('right');
		}
	// const node = this.map.current;
	}

	render() {
		return (
			<div id="body">
				<div id="content">
					<h2>Иркутская область</h2>
					<Map  ref={this.map} onClick={this.handleClick} />
				</div>
				<div id="side">
					<ul id="layers">
						<li className="region" onClick={this.handleChoose}>Районы</li>
						<li className="city" onClick={this.handleChoose}>Города</li>
						<li className="river" onClick={this.handleChoose}>Реки</li>
						<li className="place" onClick={this.handleChoose}>Места</li>
					</ul>
					<div>
						{this.state.current.id == null ? (<div onClick={this.handleStart}>Выберите слой</div> ) :
				(<div>Найдите заданный объект: <br/>
							<span>{this.state.current.name}</span><br/>
							Попыток: {this.state.current.try}</div>)}
						
					</div>		
				</div>		
			</div>
			);
	}
}


