import React from 'react';
import { Route, Redirect } from 'react-router-dom';
import * as jwt_decode from 'jwt-decode';


// import { authenticationService } from '@/_services';

const PrivateRoute = ({component: Component, roles, ...rest}) => (
	<Route {...rest} render={props => {
		// console.log(props.location)
		var user = props.location.state ? props.location.state.user : jwt_decode(localStorage.token);
		console.log(user.school)
		if (user.school && props.location.pathname === '/teacher') {
			return <Component {...props} />
		}
		if (user.school && props.location.pathname === '/students') {
			return <Component {...props} />
		}
		if (user.class && props.location.pathname === '/student') {
			return <Component {...props} />
		} else {
			return <Redirect to={{
					pathname: '/login',
					state: {
						from: props.location
					}
				}} />
		}
	}}/>
)

export default PrivateRoute;
