import React from 'react';
import { Route, Redirect } from 'react-router-dom';
import * as jwt_decode from 'jwt-decode';

const PrivateRoute = ({component: Component, roles, ...rest}) => (
	<Route {...rest} render={props => {
		// console.log(props.location)
		var user = localStorage.token;
		if (!localStorage.token && !props.location.state) {
			return <Redirect to={{
					pathname: '/login',
					state: {
						from: props.location
					}
				}} />
		}
		user = props.location.state ? props.location.state.user : jwt_decode(localStorage.token);
		if (user.school) {
			if (props.location.pathname === '/teacher' || props.location.pathname === '/students') {
				return <Component {...props} />
			}
			return <Redirect to={{
					pathname: '/teacher',
					state: {
						from: props.location
					}
				}} />
		} else if (user.class) {
			if (props.location.pathname === '/student') {
				return <Component {...props} />
			}
			return <Redirect to={{
					pathname: '/student',
					state: {
						from: props.location
					}
				}} />
		}
	}}/>
)
export default PrivateRoute;
