var panzoom = require('panzoom');
export default function svgpan() {
	console.log('ddd')

	console.log(document.getElementById('map'))


	document.addEventListener('load', function() {
		console.log('aaaa')
		var element = document.getElementById('irkutsk');
		console.log(element)
		if (element) {
			panzoom(element, {
				smoothScroll: false,
				bounds: true,
				boundsPadding: 0.1,
				maxZoom: 4,
				minZoom: 0.8
			});
		}
	});
}
