import React, { Component } from 'react';
import show from '../components/TeacherBodyJs.js';
import axios from 'axios';
import { Link } from 'react-router-dom';

export default class Teacher extends Component {
	constructor(props) {
		super(props);
		this.state = {
			user: this.props.location.state.user,
			classes: [],
			students: [],
			letter: '',
			number: '',
			class: ''
		}
		this.handleSubmit = this.handleSubmit.bind(this);
		this.handleChange = this.handleChange.bind(this);

		axios.get(`/classes`)
			.then(res => {
				this.setState({
					classes: res.data
				});
			}).catch(error => {
			console.log(error);
		});
	}

	handleSubmit(event) {
		event.preventDefault();
		console.log(this.state)
		console.log(document.getElementsByTagName('option'))

		axios.post(`/regstudents`, {
			id: this.state.user.id,
			students: this.state.students

		})
			.then(res => {
				this.setState({
					students: res.data
				});
				console.log(this.state.classes)
			}).catch(error => {
			console.log(error);
		});


	}
	handleChange(event) {
		this.setState({
			[event.target.name]: event.target.value
		});
		if (event.target.className === "class") {
			this.setState({
				[event.target.name]: event.target.options[event.target.selectedIndex].value
			});
		}
	}
	render() {
		return (
			<div id="body">
				<div id="content">
					<Link to={{
				pathname: '/students',
				state: {
					user: this.state.user
				}
			}} className="button" user={this.state.user}>Просмотреть классы</Link>
					<button onClick={show}>Создать класс</button>
				</div>
				<div id="side">
					<form action="/regstudents" method="post" id="show" style={{
				display: 'none'
			}} onSubmit={this.handleSubmit}>
						<ul>
							<li>
								<label htmlFor="number">Номер класса</label>
								<input type="text" name="number" value={this.state.number} onChange={this.handleChange} />
								</li>
							<li>
								<label htmlFor="letter">Буква</label>
								<input type="text" name="letter" value={this.state.letter} onChange={this.handleChange} />
							</li>
							<li>
								<label htmlFor="letter">Класс</label>
								<select name="class"  onChange={this.handleChange}>
								<option value=""></option>
									{this.state.classes.map(item => <option key={item.id} value={item.id}>{item.class}</option>)};
								</select>
							</li>
							<li>
								<label htmlFor="students">Список студентов</label>
								<textarea name="students" cols="30" rows="10" value={this.state.students} onChange={this.handleChange} ></textarea>
							</li>
							<li><button type="submit">Создать</button></li>
						</ul>
					</form>
				</div>
			</div>
			);
	}
}
