export const login = user => {
  console.log(user);

  return axios
    .post("/login", {
      user: {
        login: user.login,
        password: user.password,
        role: user.role
      }
    })
    .then(res => {
      localStorage.setItem('usertoken', res.data)
      return res.data
    })
    .catch(error => {
      console.log("login error", error);
    });

}

