import React, { Component } from 'react';
import show from '../components/TeacherBodyJs.js';
import axios from 'axios';
import { Link } from 'react-router-dom';

export default class Teacher extends Component {
	constructor(props) {
		super(props);
		this.state = {
			user: this.props.location.state.user,
			classes: [],
			students: [{
				name: "dds",
				password: 'dsad'
			}, {
				name: "dds",
				password: 'dsad'
			}, {
				name: "dds",
				password: 'dsad'
			}, {
				name: "dds",
				password: 'dsad'
			}],
			letter: '',
			number: '',
			class: '',
			status: ''
		}
		this.handleSubmit = this.handleSubmit.bind(this);
		this.handleCreate = this.handleCreate.bind(this);
		this.handleChange = this.handleChange.bind(this);

		axios.get(`/classes`)
			.then(res => {
				this.setState({
					classes: res.data
				});
			}).catch(error => {
			console.log(error);
		});
	}

	handleSubmit(event) {
		event.preventDefault();

		axios.post(`/regstudents`, {
			id: this.state.user.id,
			students: this.state.students,
			class: this.state.class
		})
			.then(res => {
				this.setState({
					students: res.data
				});


				console.log(this.state.students);
			}).catch(error => {
			console.log(error);
		});
	}
	handleCreate(event) {
		console.log('create')
		event.preventDefault();

		axios.post(`/createClass`, {
			id: this.state.user.id,
			number: this.state.number,
			letter: this.state.letter
		})
			.then(res => {
				this.setState({
					status: 'Создан класс:' + res.data.number + ' ' + res.data.letter
				});
			}).catch(error => {
			this.setState({
				status: ''
			});
			console.log(error);

		});
	}

	handleChange(event) {
		this.setState({
			[event.target.name]: event.target.value
		});
		if (event.target.className === "class") {
			this.setState({
				[event.target.name]: event.target.options[event.target.selectedIndex].value
			});
		}
	}
	render() {
		return (
			<div id="body">
				<div id="content">
					<Link to={{
				pathname: '/students',
				state: {
					user: this.state.user
				}
			}} className="button" user={this.state.user}>Просмотреть классы</Link>
					<button className="stud" onClick={show}>Создать учеников</button>
					<button className="class" onClick={show}>Создать класс</button>
					{this.state.students.constructor === Array ? (

				<table>
							<thead>
								<tr>
									<th>name</th>
									<th>password</th>
								</tr>
							</thead>
							<tbody>
								{this.state.students.map((stud) => <tr key={stud.name}>
									<td>{stud.name}</td>
									<td>{stud.password}</td>
								</tr>)}
							</tbody>
						</table>
				) : ('')}
		 		</div>
				<div id="side">
					<form id="stud" style={{
				display: 'none'
			}} onSubmit={this.handleSubmit}>
			<div className="success">{this.state.status}</div>
						<ul>
							<li>
								<label htmlFor="letter">Класс</label>
								<select name="class"  onChange={this.handleChange}>
								<option value=""></option>
									{this.state.classes.map(item => <option key={item.id} value={item.id}>{item.class}</option>)};
								</select>
							</li>
							<li>
								<label htmlFor="students">Список студентов</label>
								<textarea name="students" cols="30" rows="10" value={this.state.students} onChange={this.handleChange} ></textarea>
							</li>
							<li><button type="submit">Создать</button></li>
						</ul>
					</form>
						<form id="class" style={{
				display: 'none'
			}} onSubmit={this.handleCreate}>
						<ul>
							<li>
								<label htmlFor="number">Номер класса</label>
								<input type="number" name="number" value={this.state.number} onChange={this.handleChange} />
								</li>
							<li>
								<label htmlFor="letter">Буква</label>
								<input type="text" name="letter" value={this.state.letter} onChange={this.handleChange} />
							</li>
							<li><button type="submit">Создать</button></li>
						</ul>
					</form>
				</div>
			</div>
			);
	}
}
