import React, { Component } from 'react';
import show from './TeacherBodyJs.js';
import axios from 'axios';



export default class TeacherBody extends Component {

	constructor(props) {
		super(props);
		this.state = {
			students: []
		}
		this.handleSubmit = this.handleSubmit.bind(this);
	}

	handleSubmit(event) {
		// const studs = this.state;

		axios.get(`/classes`)
			.then(res => {
				this.state = res
				this.setState(res);
				// console.log(res)
			}).catch(error => {
				console.log("error");
			});
	}


	render() {
		return (
			<div id="body">
				<div id="content">
					<button onClick={this.handleSubmit}>Просмотреть классы</button>
					<button onClick={show}>Создать класс</button>
					{ console.log(this.state) }
					`{this}`
					<ul>
						{this.state.students.map(stud=><li>{stud.name}</li>)}
					</ul>
				</div>
				<div id="side">
					<form action="/regstudents" method="post" id="show" style={{display: 'none'}}>
						<ul>
							<li>
								<label htmlFor="class">Класс</label>
								<input type="text" name="class"/>
							</li>
							<li>
								<label htmlFor="students">Список студентов</label>
								<textarea name="students" cols="30" rows="10"></textarea>
							</li>
							<li><button type="submit">Создать</button></li>
						</ul>
					</form>
				</div>
			</div>
		);
	}
}
