import React, { Component } from 'react';
import Head from '../components/Head';
import { ReactComponent as MapRegion } from '../components/Map.svg';
import { ReactComponent as MapRiver } from '../components/Map.svg';

export default class Learn extends Component {
    constructor(props) {
        super(props);
        this.state = {
            login: "",
            password: "",
            role: "",
            loginError: "",
        };
        this.region = React.createRef();
        this.river = React.createRef();
        this.handleClick = this.handleClick.bind(this);
        this.handleChoose = this.handleChoose.bind(this);
        this.region.current.getElementById("river").style.display="none";

        // this.handleChange = this.handleChange.bind(this);
    }


    handleClick(event) {
        console.log(event.target.getAttribute('name'))
        console.log(event.target.getAttribute('id'))
        console.log(event.target.id);

        event.target.style.fill = "#ff0000";
    }


    handleChoose(event) {
        document.getElementById("city").style.display = "none";
        document.getElementById("river").style.display = "none";
        document.getElementById("place").style.display = "none";
        document.getElementById(event.target.className).style.display = "inline";
        const node = this.map.current;
        console.log(node.getElementById(event.target.className));

    }


    render() {
        return (
            <div>
				<Head />
				<div id="body">
					<MapRegion  ref={this.region} onClick={this.handleClick}/>
					<MapRiver  ref={this.river} onClick={this.handleClick}/>
					<ul id="layers">
						<li className="region" onClick={this.handleChoose}>Районы</li>
						<li className="city" onClick={this.handleChoose}>Города</li>
						<li className="river" onClick={this.handleChoose}>Реки</li>
						<li className="place" onClick={this.handleChoose}>Места</li>
					</ul>
				</div>
			</div>
        );
    }
}
