import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';
import * as jwt_decode from 'jwt-decode';
import UserMenu from '../components/UserMenu.js'
import PropTypes from 'prop-types';

export default class Head extends Component {
	constructor(props) {
		super(props);
		this.state = {
			user: {}
		};
		var user = localStorage.token ? jwt_decode(localStorage.token) : {};
		this.state.user = user;
	}

	render() {
		var isActive = this.context.router.route.location.pathname === this.props.to;
		var className = isActive ? 'current' : '';
		return (
			<div id="head">
				<div id="nav">
					<ul>
						<li className={className}><NavLink exact to="">О сайте</NavLink></li>
						<li className={className}><NavLink exact to="/learn">Обучение</NavLink></li>
						<li className={className}><NavLink exact to={{
				pathname: "/test",
				state: this.state
			}}>Тестирование</NavLink></li>
			 			<li><UserMenu user={this.state.user}/></li>
					</ul>
				</div>
			</div>
			);
	}
}
NavLink.contextTypes = {
	router: PropTypes.object
};
